DROP DATABASE IF EXISTS `tp_mythologie`;
CREATE DATABASE tp_mythologie;
USE tp_mythologie;
SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;

DROP TABLE IF EXISTS `articles`;
CREATE TABLE IF NOT EXISTS `articles` (
  `id_article` int(11) NOT NULL AUTO_INCREMENT,
  `id_user` int(11) NOT NULL,
  `titre_article` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `contenu_article` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `image_article` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `date_article` datetime NOT NULL DEFAULT current_timestamp(),
  PRIMARY KEY (`id_article`),
  UNIQUE KEY `titre_article` (`titre_article`),
  KEY `id_user` (`id_user`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

INSERT INTO `articles` (`id_article`, `id_user`, `titre_article`, `contenu_article`, `image_article`, `date_article`) VALUES
(1, 1, 'Achille', '&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;&lt;strong&gt;Achille&lt;/strong&gt;, &quot;semblable aux dieux&quot; mais non pas dieu lui-m&amp;ecirc;me, fut le plus fameux des h&amp;eacute;ros grecs et un acteur incontournable de la&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/troie.htm&quot;&gt;guerre de Troie&lt;/a&gt;.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Achille (Gr. \"&amp;Alpha;&amp;chi;&amp;iota;&amp;lambda;&amp;lambda;&amp;epsilon;&amp;upsilon;&amp;sigmaf; Lat. Achilleus), fils de&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/pelee.htm&quot;&gt;P&amp;eacute;l&amp;eacute;e&lt;/a&gt;, roi des Myrmidons et de la n&amp;eacute;r&amp;eacute;ide,&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/thetis.htm&quot;&gt;Th&amp;eacute;tis&lt;/a&gt;, naquit &amp;agrave; Larissa en&amp;nbsp;&lt;a class=&quot;fancybox fancybox.iframe&quot; style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; title=&quot;Carte&quot; href=&quot;https://mythologica.fr/grec/carte/a.htm&quot;&gt;Thessalie&lt;/a&gt;.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Sa&amp;nbsp;m&amp;egrave;re,&amp;nbsp;Th&amp;eacute;tis qui &amp;eacute;tait fort jolie, fut recherch&amp;eacute;e par&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/zeus.htm&quot;&gt;Zeus&lt;/a&gt;&amp;nbsp;et&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/poseidon.htm&quot;&gt;Pos&amp;eacute;idon&lt;/a&gt;&amp;nbsp;pour l\"&amp;eacute;pouser. La sage&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/themis.htm&quot;&gt;Th&amp;eacute;mis&lt;/a&gt;&amp;nbsp;savait que le fils qui na&amp;icirc;trait de Th&amp;eacute;tis serait plus grand et plus fort que son p&amp;egrave;re. Seul&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/promethee.htm&quot;&gt;Prom&amp;eacute;th&amp;eacute;e&lt;/a&gt;&amp;nbsp;connaissait ce terrible secret mais Zeus r&amp;eacute;ussit &amp;agrave; le lui arracher.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Alors, pour &amp;eacute;viter d\"&amp;ecirc;tre d&amp;eacute;tr&amp;ocirc;n&amp;eacute;s, les dieux d&amp;eacute;cid&amp;egrave;rent de marier Th&amp;eacute;tis au mortel P&amp;eacute;l&amp;eacute;e, non sans quelques difficult&amp;eacute;s car elle ne voulait pas &amp;eacute;pouser un simple mortel. En digne fille du sage N&amp;eacute;r&amp;eacute;e et comme toutes le divinit&amp;eacute;s de l\"oc&amp;eacute;an, Th&amp;eacute;tis utilisa mille formes ( le feu, l\"eau, le vent, l\"arbre, l\"oiseau, le tigre, le lion, le serpent et la seiche) pour &amp;eacute;chapper &amp;agrave; P&amp;eacute;l&amp;eacute;e qui lui m&amp;ecirc;me, conseill&amp;eacute; par le bon centaure Chiron, r&amp;eacute;ussit &amp;agrave; la saisir pour la marier. Par ce mariage leur enfant serait sup&amp;eacute;rieur &amp;agrave; son p&amp;egrave;re mais inf&amp;eacute;rieur aux immortels.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Pour calmer la d&amp;eacute;ception de Th&amp;eacute;tis d\"avoir &amp;agrave; &amp;eacute;pouser un simple mortel les dieux organis&amp;egrave;rent un somptueux&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/pelee.htm&quot;&gt;repas de mariage&lt;/a&gt;&amp;nbsp;o&amp;ugrave; toutes les divinit&amp;eacute;s (sauf une) furent convi&amp;eacute;es et apport&amp;egrave;rent de magnifiques cadeaux.&lt;br /&gt;En tant que fils de P&amp;eacute;l&amp;eacute;e il porte parfois le surnom de&amp;nbsp;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold;&quot;&gt;P&amp;eacute;l&amp;eacute;ide&lt;/span&gt;&amp;nbsp;ou d\"&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold;&quot;&gt;Eacide&lt;/span&gt;&amp;nbsp;en tant que petit-fils d\"&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/eaque.htm&quot;&gt;&amp;Eacute;aque&lt;/a&gt;.&lt;/p&gt;', 'Achille.jpg', '2022-05-03 23:58:42'),
(2, 1, 'Apollon', '&lt;p&gt;&lt;strong style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Apollon&lt;/strong&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;, (Gr. &amp;Alpha;&amp;pi;&amp;omicron;&amp;lambda;&amp;lambda;&amp;omega;&amp;nu;; Lat. Apollo) l\"une des principales divinit&amp;eacute;s de la mythologie grecque &amp;eacute;tait le dieu de la Lumi&amp;egrave;re, des Arts et de la Divination.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Il&amp;nbsp;est&amp;nbsp;fils&amp;nbsp;de&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/zeus.htm&quot;&gt;Zeus&lt;/a&gt;&amp;nbsp;et de&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/leto.htm&quot;&gt;L&amp;eacute;to&lt;/a&gt;, n&amp;eacute; sur l\"&amp;icirc;le st&amp;eacute;rile d\"Ortygie (l\"&amp;icirc;le aux cailles) qui plus tard le nom de&amp;nbsp;&lt;a class=&quot;geolien&quot; style=&quot;color: #0066ff; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/geo/delphes.htm&quot;&gt;D&amp;eacute;los&lt;/a&gt;, o&amp;ugrave; sa m&amp;egrave;re, poursuivie par la jalousie d\"H&amp;eacute;ra, avait finalement trouv&amp;eacute; refuge.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;L\"&amp;eacute;tymologie du nom d\"Apollon demeure incertaine. Aucune de celles que l\"on a propos&amp;eacute;es n\"est satisfaisante, soit qu\"on rattache ce nom &amp;agrave; un vieux verbe grec, &quot;&amp;Alpha;&amp;pi;&amp;epsilon;&amp;lambda;&amp;lambda;&amp;omega;&quot;, signifiant &quot;&amp;eacute;carter, repousser&quot;, ou &amp;agrave; une forme ancienne du verbe &quot;&amp;Alpha;&amp;pi;&amp;omicron;&amp;lambda;&amp;lambda;&amp;upsilon;&amp;mu;&amp;iota;&quot;, &quot;d&amp;eacute;truire&quot;.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;D\"ailleurs les anciens eux-m&amp;ecirc;mes connaissaient plusieurs Apollon. Cic&amp;eacute;ron&amp;nbsp;&lt;span class=&quot;fonte11&quot; style=&quot;font-size: 0.8em; line-height: 16.192px;&quot;&gt;(De natura Deorum III, 23,23)&lt;/span&gt;&amp;nbsp;en d&amp;eacute;nombre quatre.&lt;br /&gt;Aujourd\"hui, on tend &amp;agrave; distinguer un Apollon dorien et un Apollon ionien plus oriental, ancienne divinit&amp;eacute; solaire primitive comme semble l\"attester certains de ses surnoms comme Phoibos ou Xanthos (couleur de feu); mais leurs l&amp;eacute;gendes se sont m&amp;ecirc;l&amp;eacute;es au cours des si&amp;egrave;cles.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;Il est le dieu de la Lumi&amp;egrave;re, et conduit parfois le char du Soleil (&lt;/span&gt;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 17.6px; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/grec/helios.htm&quot;&gt;H&amp;eacute;lios&lt;/a&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;). A partir du Ve si&amp;egrave;cle avant notre &amp;egrave;re, les Grecs ont de plus en plus tendance &amp;agrave; l\"identifier avec H&amp;eacute;lios (le Soleil).&lt;/span&gt;&lt;br style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot; /&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;C\"est aussi un dieu agricole: &amp;agrave; Amycl&amp;eacute;es (Apollon Carneios), en Arcadie (Apollon Nomios).&lt;/span&gt;&lt;br style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot; /&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;C\"est le dieu des Purifications, le dieu vengeur qui d&amp;eacute;cha&amp;icirc;ne les &amp;eacute;pid&amp;eacute;mies, le dieu protecteur, le dieu m&amp;eacute;decin avant son fils Ascl&amp;eacute;pios.&lt;/span&gt;&lt;br style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot; /&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;C\"est le dieu des archers dont les fl&amp;egrave;ches fabriqu&amp;eacute;es par H&amp;eacute;pha&amp;iuml;stos et tir&amp;eacute;es par son arc d\"argent sont redoutables.&lt;/span&gt;&lt;/p&gt;', 'Apollon.png', '2022-05-04 00:02:04'),
(3, 1, 'Athéna', '&lt;p&gt;&lt;strong style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Ath&amp;eacute;na&lt;/strong&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;&amp;nbsp;(gr. &amp;Alpha;&amp;theta;&amp;eta;&amp;nu;&amp;eta;; Lat. Athena) est la grande d&amp;eacute;esse olympienne aux sages conseils, elle est &amp;agrave; la fois la d&amp;eacute;esse de la Guerre et la d&amp;eacute;fense des cit&amp;eacute;s, de la Sagesse, protectrice des h&amp;eacute;ros et patronne des artisans et de l\"artisanat (poterie, tissage, sculpture...)&lt;/span&gt;&lt;br style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot; /&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Elle fait partie des douze&amp;nbsp;&lt;/span&gt;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 17.6px; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/grec/olympien.htm&quot;&gt;Olympiens&lt;/a&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;. Les Romains l\"assimil&amp;egrave;rent &amp;agrave;&amp;nbsp;&lt;/span&gt;&lt;a style=&quot;color: red; font-weight: bold; font-size: 17.6px; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/rome/minerve.htm&quot; target=&quot;_blank&quot; rel=&quot;noopener&quot;&gt;Minerve&lt;/a&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Les&amp;nbsp;diverses&amp;nbsp;&amp;eacute;tymologies de son nom ne sont pas r&amp;eacute;ellement satisfaisantes. H&amp;eacute;rodote a le premier identifi&amp;eacute; Ath&amp;eacute;na avec Neith, la d&amp;eacute;esse &amp;eacute;gyptienne de Sa&amp;iuml;s; cette identification a &amp;eacute;t&amp;eacute; par la suite justifi&amp;eacute;e par toutes sortes de l&amp;eacute;gendes, telles que l\"origine &amp;eacute;gyptienne de C&amp;eacute;crops.&lt;br /&gt;De m&amp;ecirc;me l\"assimilation d\"Ath&amp;eacute;na avec la d&amp;eacute;esse perse Ana&amp;iuml;tis ou An&amp;acirc;hita d&amp;eacute;rive d\"un passage assez peu clair de Plutarque.&lt;br /&gt;Certains ont voulu retrouver dans son nom une racine sanscrite &quot;vadh&quot; qui signifie &quot;frapper&quot;.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Les origines grecques comme &quot;fleur&quot; (&amp;alpha;&amp;theta;&amp;omicron;&amp;zeta;) ou &quot;nourrice&quot; (&amp;tau;&amp;iota;&amp;theta;&amp;eta;&amp;nu;&amp;eta;) ne sont pas meilleures. Il semble, en tout cas, que le nom de la d&amp;eacute;esse et celui de la ville d\"Ath&amp;egrave;nes soient &amp;eacute;troitement li&amp;eacute;s ; l\"un d&amp;eacute;rive de l\"autre&quot;; mais on ne saurait dire lequel fut le premier.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Ses innombrables surnoms rappellent les circonstances de sa naissance, sa physionomie, ou ses attributions:&lt;br /&gt;&amp;bull; Agoraia (d&amp;eacute;esse de l\"agora),&lt;br /&gt;&amp;bull; Coryphasia (n&amp;eacute;e sur une hauteur),&lt;br /&gt;&amp;bull; Ergan&amp;eacute; (la travailleuse),&lt;br /&gt;&amp;bull; Glauk&amp;ocirc;pis (aux yeux brillants),&lt;br /&gt;&amp;bull; Hippia (protectrice des chevaux),&lt;br /&gt;&amp;bull; Nik&amp;egrave; (la victorieuse),&lt;br /&gt;&amp;bull; Pallas ou Parth&amp;eacute;nos (la jeune fille, la vierge),&lt;br /&gt;&amp;bull; Polias (gardienne de la ville),&lt;br /&gt;&amp;bull; Promachos (celle qui combat en premier, le rempart de la cit&amp;eacute;),&lt;br /&gt;&amp;bull; Pronoia (la pr&amp;eacute;voyante),&lt;br /&gt;&amp;bull; Tritog&amp;eacute;neia (n&amp;eacute;e pr&amp;egrave;s du lac Tritonis),&lt;br /&gt;ou des d&amp;eacute;tails de son culte: Apatouria, Chalciaekos, Itonia, Pandrosos, Phratia, Skiras, etc.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;L\"&amp;eacute;pith&amp;egrave;te po&amp;eacute;tique Pallas, souvent accol&amp;eacute;e au nom d\"Ath&amp;eacute;na, proviendrait soit de &amp;pi;&amp;alpha;&amp;lambda;&amp;lambda;&amp;epsilon;&amp;iota;&amp;nu;, &quot;frapper&quot;, soit, plus vraisemblablement, de &amp;pi;&amp;alpha;&amp;lambda;&amp;lambda;&amp;alpha;&amp;xi;, &quot;jeune fille&quot;.&amp;nbsp;&lt;span class=&quot;fonte11&quot; style=&quot;font-size: 0.8em; line-height: 16.192px;&quot;&gt;(Voir la fiche sur&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/pallas.htm&quot;&gt;Pallas&lt;/a&gt;&amp;nbsp;pour la l&amp;eacute;gende.)&lt;/span&gt;&lt;/p&gt;', 'Athéna.jpg', '2022-05-04 00:05:15'),
(4, 1, 'Bellérophon', '&lt;p&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;H&amp;eacute;ros corinthien,&amp;nbsp;&lt;/span&gt;&lt;strong style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Bell&amp;eacute;rophon&lt;/strong&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;&amp;nbsp;(&amp;Beta;&amp;epsilon;&amp;lambda;&amp;lambda;&amp;epsilon;&amp;rho;&amp;omicron;&amp;phi;ό&amp;nu;&amp;tau;&amp;eta;&amp;sigmaf;) fils de&amp;nbsp;&lt;/span&gt;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 17.6px; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/grec/glaucos.htm&quot;&gt;Glaucos&lt;sup&gt;1&lt;/sup&gt;&lt;/a&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;&amp;nbsp;(ou de Pos&amp;eacute;idon) r&amp;eacute;pondait &amp;agrave; sa naissance au nom de&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Hipponoos&lt;/span&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;fonte11&quot; style=&quot;font-size: 0.8em; line-height: 16.192px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot;&gt;(qui connait les chevaux)&lt;/span&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;; sa m&amp;egrave;re, qui s\"appelait soit&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Eurym&amp;eacute;d&amp;eacute;&lt;/span&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;&amp;nbsp;soit&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Eurynome&lt;/span&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;, &amp;eacute;tait la fille du roi de M&amp;eacute;gare,&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Nisos.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;&lt;span style=&quot;color: #000080; font-size: 17.6px; font-weight: 400; text-indent: 19.2px;&quot;&gt;Apr&amp;egrave;s le meurtre involontaire de son fr&amp;egrave;re, &lt;/span&gt;&lt;span class=&quot;green&quot; style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;D&amp;eacute;liad&amp;egrave;s&lt;/span&gt;&lt;span style=&quot;color: #000080; font-size: 17.6px; font-weight: 400; text-indent: 19.2px;&quot;&gt;, lors d\"un lancer de disque ou suivant d\"autres r&amp;eacute;cits, d\"un noble Corinthien tyran de son &amp;eacute;tat, nomm&amp;eacute;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;green&quot; style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;Bell&amp;eacute;ros&lt;/span&gt;&lt;span style=&quot;color: #000080; font-size: 17.6px; font-weight: 400; text-indent: 19.2px;&quot;&gt;, il dut s\"expatrier et il se retira &amp;agrave; la cour de&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;green&quot; style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;Pro&amp;eacute;tos&lt;/span&gt;&lt;span style=&quot;color: #000080; font-size: 17.6px; font-weight: 400; text-indent: 19.2px;&quot;&gt;&amp;nbsp;(Proitos ou Praetos), roi de Corinthe qui le purifia de son crime et il prit alors le nom de Bell&amp;eacute;rophon,&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;fonte11&quot; style=&quot;font-size: 0.8em; line-height: 16.192px; color: #000080; font-weight: 400; text-indent: 19.2px;&quot;&gt;&quot;tueur de Bell&amp;eacute;ros&quot;.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold;&quot;&gt;St&amp;eacute;n&amp;eacute;b&amp;eacute;e&lt;/span&gt;&amp;nbsp;(ou&amp;nbsp;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold;&quot;&gt;Ant&amp;eacute;ia&lt;/span&gt;&amp;nbsp;chez Hom&amp;egrave;re), &amp;eacute;pouse du roi, l\"accusa faussement d\"avoir tent&amp;eacute; de la s&amp;eacute;duire parce qu\"il avait refus&amp;eacute; ses avances.&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Pro&amp;eacute;tos qui ne voulut pas s\"attirer le courroux des&amp;nbsp;&lt;/span&gt;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 17.6px; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/grec/erinnyes.htm&quot;&gt;Erinyes&lt;/a&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;&amp;nbsp;en le tuant lui-m&amp;ecirc;me, envoya le h&amp;eacute;ros &amp;agrave;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Iobat&amp;egrave;s&lt;/span&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;, son beau-p&amp;egrave;re, roi de Lycie, avec une tablette scell&amp;eacute;e en lui indiquant de le faire p&amp;eacute;rir.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p class=&quot;bete&quot; style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Iobat&amp;egrave;s re&amp;ccedil;ut fort bien le jeune homme qu\"il invita &amp;agrave; sa table et au bout d\"une semaine il prit connaissance de la tablette. Ne voulant pas tuer son h&amp;ocirc;te ni contrevenir &amp;agrave; cette tr&amp;egrave;s ancienne coutume qui interdisait de tuer celui avec qui on avait partag&amp;eacute; un repas, il chercha un moyen o&amp;ugrave; il n\"aurait pas directement du sang sur les mains.&lt;/p&gt;\r\n&lt;p class=&quot;bete&quot; style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Il lui demanda de d&amp;eacute;barrasser son pays de la Chim&amp;egrave;re, persuad&amp;eacute; qu\"il succomberait dans cette lutte.&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/monstre.htm&quot;&gt;Chim&amp;egrave;re&lt;/a&gt;&amp;nbsp;&amp;eacute;tait un monstre femelle qui soufflait du feu, avait une t&amp;ecirc;te de lion, un corps avec une seconde t&amp;ecirc;te de ch&amp;egrave;vre et une queue de serpent.&lt;/p&gt;\r\n&lt;p class=&quot;bete&quot; style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Ne sachant que faire, Bell&amp;eacute;rophon alla interroger le devin Polyidos qui lui recommanda de sacrifier un taureau &amp;agrave; Pos&amp;eacute;idon et de passer une nuit dans le temple d\"Ath&amp;eacute;na.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Prot&amp;eacute;g&amp;eacute; par&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/athena.htm&quot;&gt;Ath&amp;eacute;na&lt;/a&gt;, qui lui qui lui donna un mors en or, il dompta le cheval&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/pegase.htm&quot;&gt;P&amp;eacute;gase&lt;/a&gt;. Avec une monture au vol puissant et rapide, Bell&amp;eacute;rophon tua le monstre. En volant au-dessus d\"elle, il la cribla de fl&amp;egrave;ches &amp;agrave; la pointe en plomb ou bien il lan&amp;ccedil;a un bloc de plomb dans la gueule. Le souffle ardent de Chim&amp;egrave;re fit fondre le plomb qui lui br&amp;ucirc;la les entrailles.&lt;/p&gt;', 'Bellérophon.jpg', '2022-05-04 00:07:36'),
(5, 1, 'Déméter', '&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;&lt;strong&gt;D&amp;eacute;m&amp;eacute;ter&lt;/strong&gt;&amp;nbsp;(Gr. &amp;Delta;&amp;eta;&amp;mu;&amp;eta;&amp;tau;&amp;eta;&amp;rho;; Lat. Demeter) dont le nom, sans doute une alt&amp;eacute;ration d\"un mot grec qui signifie de &quot;Terre-m&amp;egrave;re&quot;, &amp;eacute;tait la d&amp;eacute;esse de l\"agriculture et des moissons.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Elle repr&amp;eacute;sentait la terre cultiv&amp;eacute;e et f&amp;eacute;conde contrairement aux autres d&amp;eacute;esses comme Ga&amp;iuml;a ou Rh&amp;eacute;a qui personnifiait la terre en tant que mati&amp;egrave;re. C\"est elle qui facilitait la germination et la pousse des plantes.&lt;br /&gt;&lt;br /&gt;Elle &amp;eacute;tait la fille de&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/cronos.htm&quot;&gt;Cronos&lt;/a&gt;&amp;nbsp;et&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/rhea.htm&quot;&gt;Rh&amp;eacute;a&lt;/a&gt;. Elle fait partie des douze&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/olympien.htm&quot;&gt;Olympiens&lt;/a&gt;&amp;nbsp;m&amp;ecirc;me si elle pr&amp;eacute;f&amp;eacute;rait r&amp;eacute;sider &amp;agrave; Eleusis au contact de la terre que sur l\"Olympe.&lt;br /&gt;&lt;br /&gt;Elle fut assimil&amp;eacute;e par les romains sous le nom de&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/rome/ceres.htm&quot; target=&quot;_blank&quot; rel=&quot;noopener&quot;&gt;C&amp;eacute;r&amp;egrave;s&lt;/a&gt;&amp;nbsp;qui &amp;eacute;tait une divinit&amp;eacute; latine tr&amp;egrave;s ancienne associ&amp;eacute;e aux moissons.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;La base de l\"&amp;eacute;conomie grecque reposait principalement sur la culture des c&amp;eacute;r&amp;eacute;ales (orge et bl&amp;eacute;) c\"est pourquoi elle &amp;eacute;tait particuli&amp;egrave;rement v&amp;eacute;n&amp;eacute;r&amp;eacute;e en assurant l\"abondance et l\"&amp;eacute;panouissement des cit&amp;eacute;s. Elle resta toujours tr&amp;egrave;s proche des Hommes qui construisirent en son honneur un grand nombre de temples &amp;agrave; travers toute la Gr&amp;egrave;ce.&lt;/span&gt;&lt;br style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot; /&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;En tant que d&amp;eacute;esse de la terre elle avait aussi un caract&amp;egrave;re&amp;nbsp;&lt;/span&gt;&lt;abbr style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot; title=&quot;se dit de divinit&amp;eacute;s souterraines ou infernales&quot;&gt;chthonien&amp;nbsp;&lt;/abbr&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;qui fut par la suite d&amp;eacute;volu &amp;agrave; sa fille Pers&amp;eacute;phone.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;oute la Gr&amp;egrave;ce la v&amp;eacute;n&amp;eacute;rait comme une d&amp;eacute;esse majeure. Les terres &amp;agrave; bl&amp;eacute;, comme la Sicile, plus encore que les autres. Bienfaisante, elle prot&amp;eacute;geait les r&amp;eacute;coltes, elle apprit aux hommes ce qui constitue la base m&amp;ecirc;me de la civilisation : la culture du bl&amp;eacute; et la fabrication du pain. On la c&amp;eacute;l&amp;eacute;brait par des banquets champ&amp;ecirc;tres, des f&amp;ecirc;tes qui montrent son caract&amp;egrave;re populaire. Le culte &amp;eacute;tait bas&amp;eacute; sur le rythme des saisons. Certains rites rappelaient que fertilit&amp;eacute; agricole et sexualit&amp;eacute; humaine vont de pair : les fid&amp;egrave;les portaient des corbeilles avec des figurations en terre d\"organes sexuels, lan&amp;ccedil;aient des plaisanteries grivoises lors des processions. il &amp;eacute;tait &amp;agrave; la source des&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/civil/eleusinies.htm&quot;&gt;Myst&amp;egrave;res d\"Eul&amp;eacute;sis&lt;/a&gt;.&lt;br /&gt;Elle &amp;eacute;tait aussi particuli&amp;egrave;rement v&amp;eacute;n&amp;eacute;r&amp;eacute;e par les femmes, par exemple lors des&amp;nbsp;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold;&quot;&gt;Thesmophories&lt;/span&gt;&amp;nbsp;&amp;agrave;&amp;nbsp;&lt;a class=&quot;geolien&quot; style=&quot;color: #0066ff; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/geo/athenes.htm&quot;&gt;Ath&amp;egrave;nes&lt;/a&gt;, c&amp;eacute;r&amp;eacute;monie qui re&amp;ccedil;ut son nom de l\"&amp;eacute;pith&amp;egrave;te de la d&amp;eacute;esse &amp;laquo;Thesmophoros&amp;raquo; (la L&amp;eacute;gislatrice) et qui &amp;eacute;tait r&amp;eacute;serv&amp;eacute;e aux femmes ce qui ne manquait pas de faire jaser les hommes. Le secret de ses Myst&amp;egrave;res &amp;eacute;tait tr&amp;egrave;s bien gard&amp;eacute; et sa divulgation &amp;eacute;tait punie de la peine de mort; Eschyle faillit &amp;ecirc;tre condamn&amp;eacute;. Celles-ci rendaient un culte &amp;agrave; la fertilit&amp;eacute; aussi bien pour elles-m&amp;ecirc;mes que pour la cit&amp;eacute; : Aristophane en fait le sujet de sa com&amp;eacute;die, &quot;Les Thesmophories&quot;.&lt;br /&gt;Les Phigaliens adoreraient une D&amp;eacute;m&amp;eacute;ter &amp;agrave; t&amp;ecirc;te et crini&amp;egrave;re de jument, entour&amp;eacute;e de dragons et de serpents. Cette statue ayant &amp;eacute;t&amp;eacute; incendi&amp;eacute;e par accident, les Phigaliens oubli&amp;egrave;rent le culte de la d&amp;eacute;esse, qui envoya une grande s&amp;eacute;cheresse sur la pays. Cette calamit&amp;eacute; les aurait conduits jusqu\"&amp;agrave; manger leurs propres enfants, s\"ils ne l\"avaient arr&amp;ecirc;t&amp;eacute;e en r&amp;eacute;tablissant le culte de D&amp;eacute;m&amp;eacute;ter la noire.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Ses temples appel&amp;eacute;s M&amp;eacute;gara se trouvaient souvent dans les for&amp;ecirc;ts. Le serpent, animal chtonien par excellence, et le porcelet lui &amp;eacute;taient consacr&amp;eacute;s.&lt;/p&gt;', 'Déméter.jpg', '2022-05-04 00:09:59'),
(6, 1, 'Héraclès', '&lt;p&gt;&lt;strong style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;H&amp;eacute;racl&amp;egrave;s&lt;/strong&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;&amp;nbsp;(Gr. Ἡ&amp;rho;&amp;alpha;&amp;kappa;&amp;lambda;ῆ&amp;sigmaf;) est le plus c&amp;eacute;l&amp;egrave;bre h&amp;eacute;ros grec, personnification de la force; il fut identifi&amp;eacute; avec les h&amp;eacute;ros d\"autres pays : le Ph&amp;eacute;nicien Melkarth,&amp;nbsp;&lt;/span&gt;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 17.6px; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/celte/ogma.htm&quot;&gt;Ogma&lt;/a&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;, chez les Celtes,&amp;nbsp;&lt;/span&gt;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 17.6px; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/rome/hercule.htm&quot;&gt;Hercule&lt;/a&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;&amp;nbsp;chez les Latins. Maciste est parfois l\"&amp;eacute;pith&amp;egrave;te d\"H&amp;eacute;racl&amp;egrave;s car ce mot signifie tout simplement &quot;le plus grand, le plus fort&quot;&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;fonte11&quot; style=&quot;font-size: 0.8em; line-height: 16.192px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot;&gt;(Strabon VIII, 3 21)&lt;/span&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;. A l\"origine il s\"appelait&amp;nbsp;&lt;/span&gt;&lt;span class=&quot;green&quot; style=&quot;color: #007700; font-weight: bold; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;Alcide&lt;/span&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;, (qui est un patronyme tir&amp;eacute; du nom de son grand-p&amp;egrave;re, Alc&amp;eacute;e) et c\"est que plus tard qu\"Apollon ou bien la Pythie de&amp;nbsp;&lt;/span&gt;&lt;a class=&quot;geolien&quot; style=&quot;color: #0066ff; text-decoration-line: none; font-weight: bold; font-size: 17.6px; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/grec/geo/delphes.htm&quot;&gt;Delphes&lt;/a&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;,&amp;nbsp;&lt;/span&gt;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 17.6px; font-family: Verdana, Tahoma, Arial, sans-serif; text-align: justify; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/grec/xenoclee.htm&quot;&gt;X&amp;eacute;nocl&amp;eacute;e&lt;/a&gt;&lt;span style=&quot;color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px; text-align: justify; text-indent: 19.2px;&quot;&gt;, lui donna le nom d\"H&amp;eacute;racl&amp;egrave;s.&lt;/span&gt;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;H&amp;eacute;racl&amp;egrave;s descend de&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/persee.htm&quot;&gt;Pers&amp;eacute;e&lt;/a&gt;, dont le fils Alc&amp;eacute;e (le fort) donna naissance &amp;agrave; Amphitryon, p&amp;egrave;re nourricier d\"H&amp;eacute;racl&amp;egrave;s. D\"autre part, Electryon (le brillant), autre fils de Pers&amp;eacute;e, &amp;eacute;tait le p&amp;egrave;re d\"Alcm&amp;egrave;ne, (la forte). H&amp;eacute;racl&amp;egrave;s va na&amp;icirc;tre sous le double signe de la force et de la lumi&amp;egrave;re. En fait il a une parent&amp;eacute; divine puisque son p&amp;egrave;re biologique est Zeus qui voulait se donner un fils capable d\"&amp;ecirc;tre pour les Immortels comme pour les mortels un puissant protecteur. Zeus descendit nuitamment dans la ville de&amp;nbsp;&lt;a class=&quot;geolien&quot; style=&quot;color: #0066ff; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/geo/thebes.htm&quot;&gt;Th&amp;egrave;bes&lt;/a&gt;; l&amp;agrave;, ayant pris les traits d\"Amphitryon, il s\"unit &amp;agrave; son &amp;eacute;pouse. Lorsqu\"Amphitryon rentra peu apr&amp;egrave;s d\"une exp&amp;eacute;dition victorieuse, il se jeta dans les bras de son &amp;eacute;pouse. De ces deux unions successives, Alcm&amp;egrave;ne allait concevoir deux faux jumeaux: H&amp;eacute;racl&amp;egrave;s et Iphicl&amp;egrave;s.&lt;br /&gt;Sa&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/heracles1.htm&quot;&gt;naissance&lt;/a&gt;&amp;nbsp;fut difficile &amp;agrave; cause d\"H&amp;eacute;ra qui qui le retarda et qui n\"h&amp;eacute;sita pas quelques mois plus tard &amp;agrave; envoyer&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/heracles2.htm&quot;&gt;deux serpents&lt;/a&gt;&amp;nbsp;pour le d&amp;eacute;vorer dans son berceau ; mais il les &amp;eacute;touffa &amp;agrave; mains nues.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Il re&amp;ccedil;ut ensuite une solide&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/heracles3.htm&quot;&gt;&amp;eacute;ducation&lt;/a&gt;&amp;nbsp;tant militaire, sportive qu\"artistique mais ce fut un &amp;eacute;l&amp;egrave;ve difficile puisqu\"il tua son professeur de musique.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;Lorsqu\"il eut atteint sa dix-huiti&amp;egrave;me ann&amp;eacute;e H&amp;eacute;racl&amp;egrave;s fut envoy&amp;eacute; &amp;agrave; la campagne o&amp;ugrave; il entreprit de tuer le lion de Cith&amp;eacute;ron qui d&amp;eacute;cimait les troupeaux d\"Amphitryon et de son voisin, le roi&amp;nbsp;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 1em;&quot; href=&quot;https://mythologica.fr/grec/thespios.htm&quot;&gt;Thespios&lt;/a&gt;.&lt;/p&gt;\r\n&lt;p style=&quot;text-indent: 1.2rem; margin-top: 5px; text-align: justify; line-height: 23.76px; margin-right: 20px; color: #000080; font-family: Verdana, Tahoma, Arial, sans-serif; font-size: 17.6px;&quot;&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;Il re&amp;ccedil;ut en cadeau une &amp;eacute;p&amp;eacute;e de la part d\"Herm&amp;egrave;s, un arc et des fl&amp;egrave;ches de la part d\"Apollon, un plastron dor&amp;eacute; d\"H&amp;eacute;pha&amp;iuml;stos et Ath&amp;eacute;na lui remit une tunique. Mais g&amp;eacute;n&amp;eacute;ralement H&amp;eacute;racl&amp;egrave;s est facilement reconnaissable &amp;agrave; la peau de lion qui le couvre et &amp;agrave; sa massue.&lt;/span&gt;&lt;br style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot; /&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;Arm&amp;eacute; de sa massue, il extermina &amp;agrave; travers le monde, des brigands et des monstres de toutes sortes, il combattit des tyrans, et les dieux eux-m&amp;ecirc;mes. Sur la fiche &quot;&lt;/span&gt;&lt;a style=&quot;color: #9900cc; text-decoration-line: none; font-weight: bold; font-size: 17.6px; text-indent: 19.2px;&quot; href=&quot;https://mythologica.fr/grec/heracles5.htm&quot;&gt;Autour d\"H&amp;eacute;racl&amp;egrave;s&lt;/a&gt;&lt;span style=&quot;font-size: 17.6px; text-indent: 19.2px;&quot;&gt;&quot; vous trouverez la liste de tous ceux qui se trouvent m&amp;ecirc;l&amp;eacute;s aux affaires du h&amp;eacute;ros dont voici un petit r&amp;eacute;capitulatif.&lt;/span&gt;&lt;/p&gt;', 'Héraclès.jpg', '2022-05-04 00:12:07');
